/* ===== Global ModalGuard: modal open count ê¸°ë°˜ìœ¼ë¡œ body scroll lock ê´€ë¦¬ ===== */
(() => {
  const ModalGuard = (function(){
    let openCount = 0;
    let scrollY = 0;



    function lock(){
      if (openCount === 0) {
        scrollY = window.scrollY || document.documentElement.scrollTop || 0;
        const b = document.body;
        b.style.position = 'fixed';
        b.style.top = `-${scrollY}px`;
        b.style.left = '0';
        b.style.right = '0';
        b.style.width = '100%';
        b.style.overflow = 'hidden';
      }
      openCount++;
    }

    function unlock(){
      openCount = Math.max(0, openCount - 1);
      if (openCount === 0) {
        const b = document.body;
        b.style.position = '';
        b.style.top = '';
        b.style.left = '';
        b.style.right = '';
        b.style.width = '';
        b.style.overflow = '';
        window.scrollTo(0, scrollY);
      }
    }

    function isLocked(){ return openCount > 0; }
    function count(){ return openCount; }

    return { lock, unlock, isLocked, count };
  })();

  // ì „ì—­ ë…¸ì¶œ
  window.ModalGuard = ModalGuard;
})();

(() => {
  const wrap = document.getElementById('cpFirstVisitV5');
  const ok   = document.getElementById('cpfv5Ok');

  function openModal(){
    if (wrap.style.display !== 'flex') {
      wrap.style.display = 'flex';
      requestAnimationFrame(() => wrap.classList.add('show'));
      window.ModalGuard?.lock();  // âœ… ì „ì—­ ê°€ë“œë¡œ ì ê¸ˆ
      setTimeout(() => ok?.focus(), 0);
    }
  }

  function closeModal(){
    if (wrap.style.display === 'flex') {
      wrap.classList.remove('show');
      setTimeout(() => {
        wrap.style.display = 'none';
        window.ModalGuard?.unlock(); // âœ… ì „ì—­ ê°€ë“œë¡œ í•´ì œ(ë‹¤ë¥¸ ëª¨ë‹¬ì´ ì—´ë ¤ìˆìœ¼ë©´ ìœ ì§€ë¨)
      }, 220);
    }
  }

  ok.addEventListener('click', closeModal);
  wrap.addEventListener('click', e => { if (e.target === wrap) closeModal(); });
  document.addEventListener('keydown', e => { if (e.key === 'Escape') closeModal(); });

  // í˜ì´ì§€ ë¡œë“œì‹œ í‘œì‹œ (ì› ì½”ë“œ ë¡œì§ ìœ ì§€)
  if (document.readyState === 'complete' || document.readyState === 'interactive') openModal();
  else document.addEventListener('DOMContentLoaded', openModal);
})();

